source.oracleReaderQuery:
SELECT MIN(TRANSACTIONID) AS minimum_transaction_id
FROM DNACUSTOM.VW_DIGITALBANKINGTRANSACTIONS
WHERE TRANSACTIONFISCALDATE = TO_TIMESTAMP('2022-04-09T00:00:00', 'YYYY-MM-DD"T"HH24:MI:SS');

SELECT MAX(TRANSACTIONID) AS maximum_transaction_id
FROM DNACUSTOM.VW_DIGITALBANKINGTRANSACTIONS
WHERE TRANSACTIONFISCALDATE = TO_TIMESTAMP('2023-10-09T00:00:00','YYYY-MM-DD"T"HH24:MI:SS');

SELECT COUNT(*) AS transaction_count
FROM DNACUSTOM.VW_DIGITALBANKINGTRANSACTIONS
WHERE TRANSACTIONID BETWEEN 252079916 AND 332224130;
